mapper.insert:
INSERT
INTO COMMUNITY(
	BOARD_NO,
	USER_NO,
	CATEGORY_NO,
	BOARD_TYPE,
	BOARD_TITLE,
	CREATE_DATE,
	BOARD_CONTENT		
)
VALUES(
	SEQ_COM_NO.NEXTVAL		
	,#{userNo}
	,#{categoryNo}
	,#{boardType}
	,#{boardTitle}		
	,SYSDATE
	,#{boardContent}
)

INSERT
INTO COMMUNITY_FILE(
	FILE_NO,
	BOARD_NO,
	ORIGIN_NAME,
	CHANGE_NAME,
	FILE_PATH

)
VALUES(
	SEQ_COM_FILE_NO.NEXTVAL
	,SEQ_COM_NO.CURRVAL
	,#{originName}
	,#{changeName}
	,#{filePath}
)
