mapper.insert:
INSERT
INTO COMMUNITY(
	BOARD_NO,
	USER_NO,
	CATEGORY_NO,
	BOARD_TYPE,
	BOARD_TITLE,
	CREATE_DATE,
	BOARD_CONTENT		
)
VALUES(
	SEQ_COM_NO.NEXTVAL		
	,#{userNo}
	,#{categoryNo}
	,#{boardType}
	,#{boardTitle}		
	,SYSDATE
	,#{boardContent}
)

INSERT
INTO COMMUNITY_FILE(
	FILE_NO,
	BOARD_NO,
	ORIGIN_NAME,
	CHANGE_NAME,
	FILE_PATH	
)
VALUES(
	SEQ_COM_FILE_NO.NEXTVAL
	,SEQ_COM_NO.CURRVAL
	,#{originName}
	,#{changeName}
	,#{filePath}
)

INSERT
INTO COMMUNITY_FILE(
	FILE_NO,
	BOARD_NO,
	ORIGIN_NAME,
	CHANGE_NAME,
	FILE_PATH	
)
VALUES(
	SEQ_COM_FILE_NO.NEXTVAL
	,#{boardNo}
	,#{originName}
	,#{changeName}
	,#{filePath}
)

INSERT 
INTO REPORT(
	REPORT_NO,
	BOARD_NO,
	REPORT_CATE,
	USER_NO
)
VALUES(
	SEQ_REPORT_NO.NEXTVAL
	,#{boardNo}
	,#{reportCate}	
	,#{userNo}	
)

INSERT
INTO REPLY(
	REPLY_NO
	,BOARD_NO
	,USER_NO
	,REPLY_CONTENT
)
VALUES(
	SEQ_REP_NO.NEXTVAL
	,#{boardNo}
	,#{userNo}
	,#{replyContent}	
)
